_createdb () {
	read -d '' fixedservers <<"EOS"
1.0.0.1
1.1.1.1
149.112.112.112
8.8.4.4
8.8.8.8
9.9.9.9
cloudflare-dns.com
dns.google.com
dns.quad9.net
EOS
	sqlite3 "$dbloc" "create table PingServers (server TEXT, misc1 TEXT, misc2 TEXT, misc3 TEXT);" 2>/dev/null
	while read -r fixedserver
	do
		sqlite3 "$dbloc" "insert into PingServers (server) values (\"$fixedserver\");" 2>/dev/null
	done < <(echo "$fixedservers")
	allservers="$fixedservers"
}

if ! [[ -d "$supportdir" ]] ; then
	mkdir -p "$supportdir" 2>/dev/null
	_createdb
	_createicon
else
	if ! [[ -f "$dbloc" ]] ; then
		_createdb
	else
		allservers=$(sqlite3 "$dbloc" "select server from PingServers;" 2>/dev/null)
	fi
	if ! [[ -f "$icon_loc" ]] ; then
		_createicon
	fi
fi

if ! [[ -f "$prefsloc" ]] ; then
	current_server="$default_server"
	defaults write "$procid" currentServer -string "$current_server" 2>/dev/null
	absolute=true
	defaults write "$procid" absoluteMode -bool true 2>/dev/null
else
	current_server=$(/usr/libexec/PlistBuddy -c "Print:currentServer" "$prefsloc" 2>/dev/null)
	! [[ $current_server ]] && current_server="$default_server"
	if ! echo "$allservers" | grep -q "^$current_server$" &>/dev/null ; then
		allservers=$(echo -e "$allservers\n$current_server")
	fi
	if [[ $(/usr/libexec/PlistBuddy -c "Print:absoluteMode" "$prefsloc" 2>/dev/null) == "false" ]] ; then
		absolute=false
	else
		absolute=true
	fi
fi

_sysbeep () {
	osascript -e "beep" &>/dev/null
}

_notify () {
	osascript &>/dev/null << EON
tell application "System Events"
	display notification "$2" with title "$uiprocess [" & "$account" & "]" subtitle "$1"
end tell
EON
}

if [[ $1 == "addserver" ]] ; then
	while true
	do
		newserver=$(osascript 2>/dev/null << EOS
tell application "System Events"
	activate
	set theLogoPath to POSIX file "$icon_loc"
	set theNewServer to text returned of (display dialog "Please enter the new server domain or IP address." & return & return & "Examples: google.com or 8.8.8.8" 
		default answer "" 
		buttons {"Cancel", "Add"} 
		default button 2 
		cancel button "Cancel" 
		with title "$uiprocess: Add New Server" 
		with icon file theLogoPath 
		giving up after 180)
end tell
EOS
		)
		! [[ $newserver ]] && exit
		if gtimeout --preserve-status -k 3 --signal=QUIT 10 ping -c 1 -n -q "$newserver" &>/dev/null ; then
			break
		else
			_sysbeep &
			_notify " Error: offline!" "$newserver"
		fi
	done
	sqlite3 "$dbloc" "insert into PingServers (server) values (\"$newserver\");" 2>/dev/null
	exit
fi

if [[ $1 == "deleteserver" ]] ; then
	sqlite3 "$dbloc" "delete from PingServers where server=\"$2\";" &>/dev/null
	exit
fi

allfontfamilies=$(osascript 2>/dev/null << EOF
use framework "AppKit"
set fontFamilyNames to (current application's NSFontManager's sharedFontManager's availableFontFamilies) as list
return fontFamilyNames
EOF
)
if echo "$allfontfamilies" | grep -q "SF Mono" &>/dev/null ; then
	menufont="font=SFMono-Regular size=11"
else
	menufont="font=Menlo-Regular size=11"
fi

if ! command -v gtimeout &>/dev/null ; then
	echo "[ERR] | color=red $menufont dropdown=false"
	errstr=" gtimeout (coreutils) missing"
	error=true
else
	if [[ $current_server ]] ; then
		millisecs=$(gtimeout --preserve-status -k 3 --signal=QUIT 10 ping -c 3 -n -q "$current_server" 2>/dev/null | tail -n 1 | awk -F/ '{print $5}')
		if ! [[ $millisecs ]] ; then
			error=true
			routeinfo=$(route get 0.0.0.0 2>&1)
			if [[ $routeinfo ]] ; then
				if [[ $routeinfo == "route: writing to routing socket: not in table" ]] ; then
					echo "[OUT] | color=red $menufont dropdown=false"
					errstr=" Network Device Disabled"
				else
					interface=$(echo "$routeinfo" | awk -F": " '/interface:/{print $NF}' 2>/dev/null)
					if [[ $interface ]] ; then
						echo "[OFF] | color=red $menufont dropdown=false"
						errstr=" Internet Offline"
					else
						echo "[ERR] | color=red $menufont dropdown=false"
						errstr=" Network Device Missing"
					fi
				fi
			else
				echo "[ERR] | color=red $menufont dropdown=false"
				errstr=" Network Device Error"
			fi
		else
			error=false
			if $absolute ; then
				secs=$(echo "scale=3 ; x=${millisecs}/1000 ; if(x<1) print 0 ; x" | bc 2>/dev/null)
				if (( $(bc <<< "$secs < 0.100") )) ; then
					echo "$secs | $menufont dropdown=false"
				else
					if (( $(bc <<< "$secs < 1") )) ; then
						echo "$secs | color=blue $menufont dropdown=false"
					else
						echo "$secs | color=red $menufont dropdown=false"
					fi
				fi
			else
				millisecsprev=$(cat "$tmploc" 2>/dev/null)
				if ! [[ $millisecsprev ]] ; then
					echo "-.--- | color=red $menufont dropdown=false"
				else
					millisecsdiff=$(bc -l <<< "$millisecs - $millisecsprev")
					secsdiff=$(echo "scale=3 ; x=${millisecsdiff}/1000 ; if(x<1) print 0 ; x" | bc 2>/dev/null)
					if (( $(bc <<< "$secsdiff < 0.010") )) ; then
						if [[ $secsdiff =~ ^(00|0.000|0|000|0000)$ ]] ; then
							secsdiff="0.000"
						else
							secsdiff=$(echo "$secsdiff" | sed -e "s/^0-\./-0\./" -e "s/^0\./+0\./")
						fi
						echo "$secsdiff | $menufont dropdown=false"
					else
						if (( $(bc <<< "$secsdiff < 0.100") )) ; then
							echo "$secsdiff | color=blue $menufont dropdown=false"
						else
							echo "$secsdiff | color=red $menufont dropdown=false"
						fi
					fi
				fi
				echo -n "$millisecs" > "$tmploc" 2>/dev/null
			fi
		fi
	else
		error=true
		errstr=" No Server Entry!"
	fi
fi

echo "---"

if $error ; then
	echo "$errstr | color=red"
	echo "---"
else
	if $absolute ; then
		echo "Average Round-trip | size=11"
		echo "$millisecs ms"
	else
		echo "Average Round-trips | size=11"
		echo "Current: $millisecs ms"
		echo "Previous: $millisecsprev ms"
	fi
	echo "---"
fi

echo "Mode | size=11"
if $absolute ; then
	echo "Absolute | checked=true"
	echo "Switch to Relative | refresh=true terminal=false bash=/usr/bin/defaults param1=write param2=\"$procid\" param3=absoluteMode param4=-bool param5=false param6=2>/dev/null"
else
	echo "Switch to Absolute | refresh=true terminal=false bash=/usr/bin/defaults param1=write param2=\"$procid\" param3=absoluteMode param4=-bool param5=true param6=2>/dev/null"
	echo "Relative | checked=true"
fi

echo "---"

echo "Ping Servers"
if [[ $allservers ]] ; then
	while read -r server
	do
		if [[ $server == "$default_server" ]] ; then
			if [[ $server == "$current_server" ]] ; then
				echo "--$server (Default) | checked=true"
			else
				echo "--$server (Default) | refresh=true terminal=false bash=/usr/bin/defaults param1=write param2=\"$procid\" param3=currentServer param4=-string param5=\"$server\" param6=\"2>/dev/null\""
			fi
		else
			if [[ $server == "$current_server" ]] ; then
				echo "--$server | checked=true"
			else
				echo "--$server | refresh=true terminal=false bash=/usr/bin/defaults param1=write param2=\"$procid\" param3=currentServer param4=-string param5=\"$server\" param6=\"2>/dev/null\""
				echo "--Delete $server | alternate=true refresh=true terminal=false bash=$0 param1=deleteserver param2=\"$server\""
			fi
		fi
	done < <(echo "$allservers" | sort)
else
	echo "-- No Server Entries | color=red"
fi
echo "-----"
echo "--Add Server | refresh=true terminal=false bash=$0 param1=addserver"

echo "---"

echo "Refresh | refresh=true"
echo "Reset | alternate=true refresh=true terminal=false bash=$0 param1=reset"

echo "---"

pticon=""

echo "Ping Thing v$version | image=\"$pticon\""
# echo "Ping Thing v$version"
scriptpath="$0"
echo "Open in Editor | alternate=true image=\"$pticon\" terminal=false bash=/usr/bin/open param1=\"$scriptpath\""
# echo "Open in Editor | alternate=true terminal=false bash=/usr/bin/open param1=\"$scriptpath\""

exit
